countParens :: String -> Int
countParens [] = 0
countParens ('(':xs) = 1 + countParens xs
countParens (')':xs) = (-1) + countParens xs

firstNegative :: String -> Int
firstNegative s = go s 0 1 where
    go [] _ _ = -1
    go ('(':xs) floor idx = go xs (succ floor) (succ idx)
    go (')':xs) floor idx
       | floor == 0 = idx
       | otherwise = go xs (pred floor) (succ idx)
                       
